    fun getRandomQuote(selectedCategories: Set<String> = setOf("Peace & Comfort", "God's Glory & Sovereignty", "The Gospel & Grace", "Wisdom & Living")): Quote {
        val filteredQuotes = if (selectedCategories.isEmpty()) {
            quotes
        } else {
            quotes.filter { it.category in selectedCategories }
        }
        
        return if (filteredQuotes.isNotEmpty()) {
            filteredQuotes.random()
        } else {
            // Fallback if no quotes match (shouldn't happen, but safety)
            quotes.random()
        }
    }
